class Solution:

    def concat_words(self, lst, index, s, words, result):
        if index == len(lst):
            sub = ''
            for index in lst:
                sub += words[index]
            # 找出所有的索引
            start = 0
            while True:

                try:
                    if sub in s[start:]:
                        loc = s[start:].index(sub)
                        result.append(loc + start)
                        start = loc + start + 1
                    else:
                        break
                except:
                    break
            print(lst)
            return
        else:
            for i in range(len(lst)):
                if i in lst[:index + 1]:
                    continue
                else:
                    lst[index] = i
                    self.concat_words(lst, index + 1, s, words, result)

    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if s == '' or words == []:
            return []
        for word in words:
            if word not in s:
                return []
        lst = [None] * len(words)
        result = []
        self.concat_words(lst, 0, s, words, result)
        return list(set(result))


